import React from 'react';
import './searchModule.css';

export const SearchModule = (props) => {

  let className = 'searchBar';
  if(props.isVisible) {
    className += ' click';
  }else{
    className -= ' click';
  }

  return(

    <div class="searchModule">

      <input style={{display: props.isVisible ? "block" : "none"}} className="searchBar" placeholder="Search" type="search"></input>

    </div>
  );
}

export default SearchModule;
