import React, { Component } from 'react';
import ScrubBar from './components/scrubBar/scrubBar.jsx';

class App extends Component {

  constructor() {
    super();

    this.state = {
      body: "",
      songNames: [],
      songPictures: [],
    }
  }

  async componentDidMount() {

    const response = await fetch("http://localhost:5000")
    const data = await response.json()

    //console.log(data);

    data.forEach(song => {
      let joined = this.state.songNames.concat(song.result.full_title)
      this.setState({songNames: joined})
      //this.state.songNames.push(song)
    })

    data.forEach(picture => {
      let joined = this.state.songPictures.concat(picture.result.song_art_image_thumbnail_url)
      this.setState({songPictures: joined})
    })

    console.log(this.state.songPictures);

}

  render() {
    return (
      <div className="main">

        <p> {this.state.body} </p>

        <div className="sideNav">
          <div className="sideNavButton">Home</div>
          <div className="sideNavButton">Search</div>
        </div>

        <div className="content">
          {
            this.state.songPictures.map(songPicture =>
            <div className="contentItem">
              <img src="" className="contentItemPicture"></img>
              <div className="contentItemName"></div>
            </div>
          )
          }
        </div>

        <ScrubBar className="scrubBar" />

      </div>
    );
  }

}

export default App;
