import React, { Component } from 'react';
import ScrubBar from './components/scrubBar/scrubBar.jsx';

class App extends Component {

  constructor() {
    super();

    this.state = {
      body: "",
      songNames: [],
    }
  }

  async componentDidMount() {

    const response = await fetch("http://localhost:5000")
    const data = await response.json()

    //console.log(data);

    data.forEach(song => {
      let joined = this.state.songNames.concat(song.result.full_title)
      this.setState({songNames: joined})
      //this.state.songNames.push(song)
    })

    console.log(this.state.songNames);

}

  render() {
    return (
      <div className="main">

        <p> {this.state.body} </p>

        <div className="sideNav">
          <div className="sideNavButton">Home</div>
          <div className="sideNavButton">Search</div>
        </div>

        <div className="content">
          {
            this.state.songNames.map(songName =>

            <div className="contentItem">{songName}</div>
          )
          }
        </div>

        <ScrubBar className="scrubBar" />

      </div>
    );
  }

}

export default App;
